import React, { Component } from "react";
import styles from "./WeatherCard.css";

export default class WeatherCard extends Component {
  state = {
    weatherData: "",
    error: null
  };

  convertKelvinToCelsius = kelvin => {
    const celsius = kelvin - 273.15;
    return celsius.toFixed(2);
  };

  componentDidMount() {
    let cityID = this.props.city;
    let api = "0f33f5c78acf44e7d38b5f6706f6f59d";
    let weatherURL = `https://api.openweathermap.org/data/2.5/weather?id=${cityID}&appid=${api}`;
    fetch(weatherURL)
      .then(response => response.json())
      .then(weatherData => {
        this.setState({ weatherData });
      })
      .catch(error => {
        this.setState({ error });
      });
  }

  render() {
    console.log(this.state.weatherData);
    return (
      <section className={styles.card}>
        <h1>Weather for: </h1>
        {!this.state.weatherData ? (
          <p>Data are not availabe</p>
        ) : (
          //   <article>
          //     <h2>{this.state.weatherData.name} </h2>
          //   <p>
          //     Current Temperature:{" "}
          //     {this.convertKelvinToCelsius(this.state.weatherData.main.temp)}°C
          //   </p>
          //   <p>Humidity: {this.state.weatherData.main.humidity}% </p>
          //   <p>
          //     Min Temperature:{" "}
          //     {this.convertKelvinToCelsius(
          //       this.state.weatherData.main.temp_min
          //     )}
          //     °C
          //   </p>
          //   <p>
          //     Max Temperature:{" "}
          //     {this.convertKelvinToCelsius(
          //       this.state.weatherData.main.temp_max
          //     )}
          //     °C
          //   </p>
          //   </article>
          <article>
               <div class="card-header">Today weather for:</div>
          <div class="card text-white bg-dark mb-3" style="max-width: 18rem;">
            {/* <div class="card-header">Today weather for:</div> */}
            <div class="card-body">
              <h5 class="card-title">{this.state.weatherData.name}</h5>
              <p class="card-text">
                <p>
                  Current Temperature:{" "}
                  {this.convertKelvinToCelsius(
                    this.state.weatherData.main.temp
                  )}
                  °C
                </p>
                <p>Humidity: {this.state.weatherData.main.humidity}% </p>
                <p>
                  Min Temperature:{" "}
                  {this.convertKelvinToCelsius(
                    this.state.weatherData.main.temp_min
                  )}
                  °C
                </p>
                <p>
                  Max Temperature:{" "}
                  {this.convertKelvinToCelsius(
                    this.state.weatherData.main.temp_max
                  )}
                  °C
                </p>
              </p>
            </div>
          </div>
          </article>
        )}
      </section>

    );
  }
}
